mapper.select:
SELECT c.comment_id AS commentId, c.post_id AS postId, c.user_id AS userId, m.nickname AS nickname, c.content AS commentContent, c.created_at AS createdAt, c.is_deleted AS isDeleted FROM community_comment c JOIN member m ON c.user_id = m.member_id WHERE 1=1
AND c.content LIKE CONCAT('%', #{keyword}, '%')
AND c.user_id = #{userId}
ORDER BY c.created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM community_comment

    AND content LIKE CONCAT('%', #{keyword}, '%')


    AND user_id = #{userId}

SELECT EXISTS (
SELECT 1 FROM comment_like
WHERE comment_id = #{commentId}
AND user_id = #{userId}
)
