mapper.select:
SELECT post_id AS postId, user_id AS userId, title, content, is_notice AS isNotice, created_at AS createdAt, updated_at AS updatedAt, is_deleted AS isDeleted FROM community_post WHERE is_deleted = 'N' AND is_notice = 'N'
AND (title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%'))
AND user_id = #{userId}
ORDER BY created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM community_post WHERE is_deleted = 'N' AND is_notice = 'N'
AND (title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%'))
AND user_id = #{userId}

SELECT image_url
FROM post_image
WHERE post_id = #{postId}

SELECT p.post_id AS postId, p.user_id AS userId, m.nickname AS nickname, p.title, p.content, p.is_notice AS isNotice, p.created_at AS createdAt, p.updated_at AS updatedAt, p.deleted_at AS deletedAt, p.is_deleted AS isDeleted FROM community_post p JOIN member m ON p.user_id = m.member_id
1 = 1
AND (p.title LIKE CONCAT('%', #{keyword}, '%') OR p.content LIKE CONCAT('%', #{keyword}, '%'))
AND p.user_id = #{userId}
ORDER BY p.created_at DESC LIMIT #{limit} OFFSET #{offset}
