mapper.select:
SELECT participation_id, status_id, meeting_id, member_id, participated_at
FROM meeting_participation_history
WHERE meeting_id = #{ meetingId }
AND member_id = #{ memberId }
AND status_id = 2

SELECT participation_id, status_id, meeting_id, member_id, participated_at
FROM meeting_participation_history
WHERE meeting_id = #{ meetingId }
AND status_id = #{ statusId }

SELECT participation_id, status_id, meeting_id, member_id, participated_at
FROM meeting_participation_history
WHERE meeting_id = #{ meetingId }
AND member_id = #{ memberId }
