mapper.select:
SELECT p.place_id AS placeId, p.place_name AS placeName, p.address AS address, COALESCE(AVG(r.review_score), 0) AS reviewRating FROM place p LEFT JOIN place_review r ON p.place_id = r.place_id WHERE 1 = 1
AND p.sport_id = #{sportId}
AND p.address LIKE CONCAT('%', #{address}, '%')
AND p.is_active = 'Y' GROUP BY p.place_id, p.place_name, p.address ORDER BY p.place_id DESC LIMIT #{limit} OFFSET #{offset}

SELECT place_id, place_name, address, description, equipment, rental_cost
FROM place
WHERE place_id = #{placeId}

SELECT p.place_id AS placeId, p.place_name AS placeName, p.address AS address, COALESCE(AVG(r.review_score), 0) AS reviewRating FROM place p LEFT JOIN place_review r ON p.place_id = r.place_id WHERE 1 = 1
AND p.sport_id = #{sportId}
AND p.address LIKE CONCAT('%', #{address}, '%')
AND p.is_active = #{isActive}
GROUP BY p.place_id, p.place_name, p.address ORDER BY p.place_id DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM place p
WHERE 1 = 1

    AND p.sport_id = #{sportId}


    AND p.address LIKE CONCAT('%', #{address}, '%')


    AND p.is_active = #{isActive}

SELECT p.place_id AS placeId, p.place_name AS placeName, p.address AS address, COALESCE(AVG(r.review_score), 0) AS reviewRating FROM place p LEFT JOIN place_review r ON p.place_id = r.place_id WHERE 1 = 1 AND p.owner_id = #{ownerId}
AND p.sport_id = #{sportId}
AND p.address LIKE CONCAT('%', #{address}, '%')
GROUP BY p.place_id, p.place_name, p.address ORDER BY p.place_id DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM place p
WHERE 1 = 1
AND p.owner_id = #{ownerId}

    AND p.sport_id = #{sportId}


    AND p.address LIKE CONCAT('%', #{address}, '%')
