mapper.select:
SELECT o.objection_id AS objectionId, o.penalty_id AS penaltyId, o.member_id AS memberId, u.user_name AS userName, CASE WHEN o.status_id = 1 THEN '처리중' WHEN o.status_id = 2 THEN '처리완료' WHEN o.status_id = 3 THEN '거절됨' ELSE '알 수 없음' END AS statusId, o.reason, o.created_at AS createdAt FROM objection o JOIN user u ON o.member_id = u.user_id WHERE 1 = 1
AND o.status_id = #{statusId}
AND o.member_id = #{memberId}
AND o.penalty_id = #{penaltyTypeId}
ORDER BY o.created_at DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM objection o
WHERE 1 = 1

    AND o.status_id = #{statusId}


    AND o.member_id = #{memberId}


    AND o.penalty_id = #{penaltyTypeId}

SELECT o.objection_id AS objectionId, o.penalty_id AS penaltyId, o.member_id AS memberId, u.user_name AS userName, CASE WHEN o.status_id = 1 THEN '처리중' WHEN o.status_id = 2 THEN '처리완료' WHEN o.status_id = 3 THEN '거절됨' ELSE '알 수 없음' END AS statusId, o.reason AS reason, o.created_at AS createdAt,
p.penalty_type AS penalty_type, p.post_id AS post_id, p.comment_id AS comment_id, p.review_id AS review_id, p.reason AS penalty_reason, p.is_active AS is_active, p.created_at AS penalty_created_at
FROM objection o JOIN user u ON o.member_id = u.user_id JOIN user_penalty_history p ON o.penalty_id = p.penalty_id WHERE o.objection_id = #{objectionId}
