mapper.select:
SELECT ph.penalty_id, ph.user_id, u.user_name AS user_name, ph.penalty_type AS penaltyType, ph.reason, ph.created_at, CASE ph.status_id WHEN 1 THEN '처리중' WHEN 2 THEN '처리완료' WHEN 3 THEN '거절됨' ELSE '알 수 없음' END AS statusId, ph.post_id, ph.comment_id, ph.review_id FROM user_penalty_history ph JOIN user u ON ph.user_id = u.user_id WHERE 1 = 1
AND ph.penalty_type = #{penaltyType}
AND ph.user_id = #{userId}
AND ph.status_id = #{statusId}
ORDER BY ph.created_at DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM user_penalty_history ph
JOIN user u ON ph.user_id = u.user_id
WHERE 1 = 1

    AND ph.penalty_type = #{penaltyType}


    AND ph.user_id = #{userId}


    AND ph.status_id = #{statusId}
