mapper.select:
SELECT rh.report_id, rh.reporter_member_id, ru.user_name AS reporter_name, rh.target_member_id, tu.user_name AS target_name, rh.report_type_id, rt.report_type AS report_type, CASE rh.status_id WHEN 1 THEN '처리중' WHEN 2 THEN '처리완료' WHEN 3 THEN '거절됨' ELSE '알 수 없음' END AS status, rh.reason, rh.post_id, rh.comment_id, rh.created_at FROM report_history rh JOIN report_type rt ON rh.report_type_id = rt.report_type_id JOIN member rm ON rh.reporter_member_id = rm.member_id JOIN user ru ON rm.member_id = ru.user_id JOIN member tm ON rh.target_member_id = tm.member_id JOIN user tu ON tm.member_id = tu.user_id WHERE 1=1
AND rh.reporter_member_id = #{req.reporterMemberId}
AND rh.target_member_id = #{req.targetMemberId}
AND rh.report_type_id = #{req.reportTypeId}
AND rh.status_id = #{req.statusId}
ORDER BY rh.created_at DESC LIMIT #{req.size} OFFSET #{req.offset}

SELECT COUNT(*)
FROM report_history rh
WHERE 1=1

    AND rh.reporter_member_id = #{req.reporterMemberId}


    AND rh.target_member_id = #{req.targetMemberId}


    AND rh.report_type_id = #{req.reportTypeId}


    AND rh.status_id = #{req.statusId}
