mapper.select:
SELECT EXISTS (
SELECT 1
FROM member
WHERE member_id = #{userId}
)

SELECT u.user_id, u.user_name, u.email, m.nickname, r.role_name, s.status_type, u.point_balance, u.contact_number FROM user u LEFT JOIN member m ON m.member_id = u.user_id LEFT JOIN role r ON u.role_id = r.role_id LEFT JOIN status s ON u.status_id = s.status_id
AND r.role_name = #{roleName}
AND s.status_type = #{statusName}
ORDER BY u.user_id DESC
